/**
 * In large parts stolen from https://github.com/Redundakitties/colorful-minimalist/
 */

/* ----------- Variables ----------- */

/* Colors (taken from acorn.firefox.com) */
:root {
    --primary-light: #00b3f4;
    --primary-dark: #0060df;
    --primary: var(--primary-light);

    --success-light: #2ac3a2;
    --success-dark: #b3ffe3;
    --success: var(--success-light);

    --warning-light: #ffa436;
    --warning-dark: #ffea80;
    --warning: var(--warning-light);

    --critical-light: #c50042;
    --critical-dark: #e22850;
    --critical: var(--critical-light);

    --light-gray-light: #bfbfc9;
    --light-gray-dark: #bfbfc9;
    --light-gray: var(--light-gray-light);

    --dark-gray-light: #3a3944;
    --dark-gray-dark: #5b5b66;
    --dark-gray: var(--dark-gray-light);
}

/* Set colors based on color scheme */
@media (prefers-color-scheme: dark) {
    :root {
        --primary: var(--primary-dark);
        --success: var(--success-dark);
        --warning: var(--warning-dark);
        --critical: var(--critical-dark);
        --light-gray: var(--light-gray-dark);
        --dark-gray: var(--dark-gray-dark);
    }
}

/* Tab bar values */
:root {
    --nav-width: 40px;
    --fav-horiz-margin: 13px;
    --fav-size: 16px;
    --close-size: 28px;

    --min-tab-offset: calc(var(--nav-width)
    + var(--fav-horiz-margin)
    + var(--fav-size));
    --min-margin-correction: calc(100vw
    - var(--min-tab-offset)
    - 100%);
}

/* Set colors and indents on the sidebar root element */
#root.root {
    --frame-el-overlay-hover-bg: var(--primary);

    /* Larger tabs and tab indents. */
    --tabs-indent: 16px;
    --tabs-height: 32px;
}

.Tab .fav {
    height: var(--fav-size);
    width: var(--fav-size);
}

.Tab .close {
    height: var(--close-size);
    width: var(--close-size);
}

.Tab .close:hover {
    background: var(--critical) !important;
}


/* Hide counts on folded branches and groups */
.Tab .fav > .child-count {
    display: none;
}

/* ----------- Tab title styles ----------- */
/* Display container indicator on the left. */
.Tab .ctx {
    left: 1px;
}

/* Dark gray background and strike through for unloaded tabs */
.Tab[data-discarded="true"] .title {
    --tabs-normal-fg: var(--dark-gray);
    text-decoration: line-through;
}

/* Light gray background and italic font for unread tabs */
.Tab[data-unread="true"] .title {
    --tabs-normal-fg: var(--light-gray);
    font-style: italic;
}

/* Add dropshadow to favicon. */
.Tab .fav > .fav-icon {
    width: var(32px);
    height: var(32px);
    filter: drop-shadow(0px 0px 2px rgba(0, 0, 0, 0.4)) !important;
}

/* Tighter offset for the audio indicator */
.Tab .audio {
    top: 3px !important;
    left: 10px !important;
    filter: drop-shadow(0px 0px 1px black);
}

/* Tighter offset for the audio indicator on pinned tabs. */
.Tab[data-pin="true"] .audio {
    top: 12px !important;
    left: 15px !important;
    filter: drop-shadow(0px 0px 1px black);
}

/* ----------- Tab colors ----------- */


.Tab,
.PinnedTab {
    --tabs-activated-bg: var(--primary);
    --frame-el-overlay-hover-bg: var(--primary);
    /* TODO: target active tab with font-style: italic;*/
}

/* ----------- Pinned Tab Bar Shadow ----------- */
.PinnedTabsBar {
    box-shadow: inset -2px 0px 30px rgba(0, 0, 0, 0.23);
}

#root[data-pinned-tabs-position="panel"] .PinnedDock {
    justify-content: flex-end;
}

#root[data-pinned-tabs-position="panel"]:not(:hover) .PinnedDock {
    flex-direction: row-reverse;
}


/* ----------- Tab title scrolling ----------- */

/* Set --tree-lvl variable based on indentation level. */
/* @formatter:off */
.Tab[data-lvl="0"], .pointer[data-lvl="0"] { --tree-lvl: 0; }
.Tab[data-lvl="1"], .pointer[data-lvl="1"] { --tree-lvl: 1; }
.Tab[data-lvl="2"], .pointer[data-lvl="2"] { --tree-lvl: 2; }
.Tab[data-lvl="3"], .pointer[data-lvl="3"] { --tree-lvl: 3; }
.Tab[data-lvl="4"], .pointer[data-lvl="4"] { --tree-lvl: 4; }
.Tab[data-lvl="5"], .pointer[data-lvl="5"] { --tree-lvl: 5; }
.Tab[data-lvl="6"], .pointer[data-lvl="6"] { --tree-lvl: 6; }
.Tab[data-lvl="7"], .pointer[data-lvl="7"] { --tree-lvl: 7; }
.Tab[data-lvl="8"], .pointer[data-lvl="8"] { --tree-lvl: 8; }
.Tab[data-lvl="9"], .pointer[data-lvl="9"] { --tree-lvl: 9; }
.Tab[data-lvl="10"], .pointer[data-lvl="10"] { --tree-lvl: 10; }
/* @formatter:on */

div.NavigationBar {
    width: var(--nav-width) !important;
}

div.panel-btn {
    width: var(--nav-width) !important;
    height: var(--nav-width) !important;
}

.Tab .t-box {
    min-width: fit-content;
}

.Tab .t-box .title {
    transition: all 1.5s ease;
    display: inline-block;
    padding-right: var(--close-size);
}

.Tab:hover .title {
    --margin-correction: calc(var(--min-margin-correction)
    - (var(--tabs-indent) * var(--tree-lvl)));

    margin-left: min(var(--margin-correction), 0px);
}

